import React, { useState, useRef, useEffect } from 'react';
import '../styles/AskAI.css';

function AskAI() {
  const [messages, setMessages] = useState([
    { type: 'bot', text: "Hi! I'm Wavi, your disaster awareness assistant. How can I help you today?" }
  ]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim()) return;

    // Add user message
    setMessages(prev => [...prev, { type: 'user', text: input }]);

    // Simulate AI response (replace with actual AI integration)
    setTimeout(() => {
      setMessages(prev => [...prev, {
        type: 'bot',
        text: `I understand your concern about ${input}. Let me help you with that...`
      }]);
    }, 1000);

    setInput('');
  };

  return (
    <section id="ask-ai" className="ai-section">
      <div className="mascot-container">
        <div className="mascot-wrapper">
          <img 
            src="/images/wavi-mascot.png" 
            alt="Wavi the Wave" 
            className="mascot"
          />
        </div>
      </div>
      
      <div className="chat-container">
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.type}`}>
              {msg.text}
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>
        
        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Ask me anything about disasters..."
          />
          <button onClick={handleSend}>Send</button>
        </div>
      </div>
    </section>
  );
}

export default AskAI;
