import React, { useState } from 'react';
import '../styles/ExperienceShare.css';

function ExperienceShare() {
  const [experiences, setExperiences] = useState([]);
  const [newExperience, setNewExperience] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!newExperience.trim()) return;

    const experience = {
      id: Date.now(),
      text: newExperience,
      date: new Date().toLocaleDateString(),
      likes: 0
    };

    setExperiences([experience, ...experiences]);
    setNewExperience('');
  };

  return (
    <section id="experiences" className="share-experience">
      <h2>Share Your Experience</h2>
      <div className="experience-container">
        <form onSubmit={handleSubmit} className="experience-form slide-up">
          <textarea
            value={newExperience}
            onChange={(e) => setNewExperience(e.target.value)}
            placeholder="Share your disaster-related experience..."
            rows="4"
          />
          <button type="submit" className="submit-button">
            Share Story
          </button>
        </form>

        <div className="experiences-list">
          {experiences.map((exp) => (
            <div key={exp.id} className="experience-card fade-in">
              <p>{exp.text}</p>
              <div className="experience-footer">
                <small>Posted on {exp.date}</small>
                <button 
                  className="like-button"
                  onClick={() => {
                    setExperiences(experiences.map(e => 
                      e.id === exp.id ? {...e, likes: e.likes + 1} : e
                    ));
                  }}
                >
                  ❤️ {exp.likes}
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default ExperienceShare;
