import React, { useState, useRef, useEffect } from 'react';
import { GoogleGenerativeAI } from '@google/generative-ai';
import '../styles/AskAI.css';

function AskAI() {
  // Direct URL for the mascot image
  const waviMascot = "https://source.unsplash.com/300x300/daily?wave";
  
  const [messages, setMessages] = useState([
    { type: 'bot', text: "Hi! I'm Wavi, your disaster awareness assistant. How can I help you today?" }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    // Add user message
    const userMessage = { type: 'user', text: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      // Initialize the API
      const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY);
      const model = genAI.getGenerativeModel({ model: "gemini-pro"});

      // Generate content
      const prompt = `You are Wavi, a friendly disaster awareness assistant. 
                     Provide a concise and practical response to: ${input}
                     Focus on disaster awareness and safety measures.
                     Keep the response under 100 words.`;

      const result = await model.generateContent(prompt);
      const response = await result.response;
      const text = response.text();

      const botMessage = {
        type: 'bot',
        text: text
      };
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, {
        type: 'bot',
        text: "I'm sorry, I'm having trouble connecting right now. Please try again later."
      }]);
    }

    setIsLoading(false);
  };

  return (
    <section id="ask-ai" className="ai-section">
      <div className="mascot-container">
        <div className="mascot-wrapper">
          <img 
            src={waviMascot} 
            alt="Wavi the Wave" 
            className="mascot"
          />
        </div>
      </div>
      
      <div className="chat-container">
        <div className="chat-messages">
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.type}`}>
              {msg.text}
            </div>
          ))}
          {isLoading && (
            <div className="message bot loading">
              <span className="dot"></span>
              <span className="dot"></span>
              <span className="dot"></span>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        
        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Ask me anything about disasters..."
            disabled={isLoading}
          />
          <button 
            onClick={handleSend}
            disabled={isLoading}
            className={isLoading ? 'loading' : ''}
          >
            {isLoading ? 'Sending...' : 'Send'}
          </button>
        </div>
      </div>
    </section>
  );
}

export default AskAI;
