import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/DisasterGrid.css';

const disasters = [
  {
    type: 'earthquake',
    title: 'Earthquake',
    video: '/videos/earthquake.mp4',
    description: 'Learn about earthquake safety and preparedness'
  },
  {
    type: 'tsunami',
    title: 'Tsunami',
    video: '/videos/tsunami.mp4',
    description: 'Understand tsunami warnings and evacuation'
  },
  {
    type: 'drought',
    title: 'Drought',
    video: '/videos/drought.mp4',
    description: 'Discover drought management and water conservation'
  },
  {
    type: 'forest-fire',
    title: 'Forest Fires',
    video: '/videos/forest-fire.mp4',
    description: 'Learn about fire safety and prevention'
  }
];

function DisasterGrid() {
  const navigate = useNavigate();

  return (
    <section id="disasters" className="disaster-grid">
      {disasters.map((disaster) => (
        <div 
          key={disaster.type}
          className={`disaster-card ${disaster.type}`}
          onClick={() => navigate(`/disaster/${disaster.type}`)}
        >
          <video className="background-video" autoPlay muted loop>
            <source src={disaster.video} type="video/mp4" />
          </video>
          <div className="overlay">
            <h2>{disaster.title}</h2>
            <p>{disaster.description}</p>
            <button className="learn-more">Learn More</button>
          </div>
        </div>
      ))}
    </section>
  );
}

export default DisasterGrid;
